using Action.VewModels;
using Microsoft.AspNetCore.Http;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Threading.Tasks;
using Action.Models;
using IBM.WatsonDeveloperCloud.Http.Extensions;

namespace Action.Extensions
{
	public static class ImageUpload
	{
		public static string GenerateFileRoute(string filename, Stream stream, HttpRequest request, ApplicationDbContext context)
		{
			stream.Position = 0;
			var bytes = stream.ReadAllBytes();
			var img = new ImageRepo
			{
				Id = new Guid(filename.Split('.')[0]),
				Base64Image = Convert.ToBase64String(bytes),
				ImageName = filename
			};
			context.Images.Add(img);
			context.SaveChanges();
			var route = "";
			route = request.IsHttps ? "Https://" : "Http://";
			route += $"{request.Host}/api/entities/image/{ filename }";
			return route;
		}
		
		
		public static string GenerateImageRoute(ImageRequest model, HttpRequest pRequest)
		{
			if (string.IsNullOrWhiteSpace(model?.Base64Image) || string.IsNullOrWhiteSpace(model.ImageName))
				return null;
			var route = "";
			if (pRequest.IsHttps)
				route = "Https://";
			else
				route = "Http://";

			route += $"{pRequest.Host}/api/entities/image/{ SaveImageToDisk(model.Base64Image, model.ImageName)}";
			return route;
		}

		private static string SaveImageToDisk(string pImagem, string fileName)
		{
			if (String.IsNullOrEmpty(pImagem))
				pImagem = "";

			var bytes = Convert.FromBase64String(pImagem);
			var imageid = Guid.NewGuid().ToString();
			var lPath = Directory.GetCurrentDirectory() + @"wwwroot\imagens\" + imageid + fileName.GetFileExtension();

			using (var imageFile = new FileStream(lPath, FileMode.Create))
			{
				imageFile.Write(bytes, 0, bytes.Length);
				imageFile.Flush();
			}
			return imageid + fileName.GetFileExtension();
		}
	}
}
